mapper.select:
SELECT a.startTime, o.regionId,o.name as regOptName,o.hid, o.age,o.sex,o.ageMon,o.ageDay, o.designedOptName,o.designedAnaesMethodName,o.diagnosisName,m.name as operRoomName,a.operRoomId,o.bed, o.realoperationName,o.operaDate, IF(o.deptName IS NULL OR o.deptName = '' , (SELECT NAME FROM bas_dept t WHERE t.deptId = o.deptId ) ,o.deptName) deptName, a.optBody,(select codeName from bas_sys_code where groupId='opt_body' and codeValue = a.optBody and beid = #{baseQuery.beid}) as opt_body_name, (select name from bas_user where bas_user.userName = a.instrnurseId1 and beid = #{baseQuery.beid} )instrnurse_name1,instrnurseId1 , (select name from bas_user where bas_user.userName = a.circunurseId1 and beid = #{baseQuery.beid} )circunurse_name1,circunurseId1, (select name from bas_user where bas_user.userName = a.anesthetistId and beid = #{baseQuery.beid} )anesthetist_name,anesthetistId, (select name from bas_user where bas_user.userName = a.circuanesthetistId and beid = #{baseQuery.beid})circuanesthetist_name,circuanesthetistId, (select name from bas_user where bas_user.userName = a.perfusionDoctorId and beid = #{baseQuery.beid})perfusiondoctor_name,perfusionDoctorId, (select name from bas_user where bas_user.userName = a.instrnurseId2 and beid = #{baseQuery.beid} )instrnurse_name2,instrnurseId2, (select name from bas_user where bas_user.userName = a.circunurseId2 and beid = #{baseQuery.beid})circunurse_name2,circunurseId2, (select name from bas_user where bas_user.userName = a.healthnurse and beid = #{baseQuery.beid} )healthnurse_name,a.healthnurse, o.operaDate , o.state ,a.isHold ,a.operRoomId,a.regOptId,o.emergency,a.pcs seq ,a.pcs,o.remark, SUBSTR(o.operatorName,LOCATE(',',o.operatorName)+1) operatorName,o.isLocalAnaes,o.assistantName, SUBSTRING(o.operatorName,LOCATE(',',o.operatorName)+1) operator_sec_name, CONCAT_WS(',',(select name from bas_user where bas_user.userName = a.instrnurseId1 and beid = #{baseQuery.beid} ), (select name from bas_user where bas_user.userName = a.instrnurseId2 and beid = #{baseQuery.beid} )) instrnurse_name, CONCAT_WS(',',(select name from bas_user where bas_user.userName = a.circunurseId1 and beid = #{baseQuery.beid} ), (select name from bas_user where bas_user.userName = a.circunurseId2 and beid = #{baseQuery.beid})) circunurse_name FROM bas_dispatch a left join bas_reg_opt o on a.regOptId = o.regOptId and o.state !='08'
left join bas_operroom m on a.operRoomId = m.operRoomId where 1 = 1 and a.beid = #{baseQuery.beid} and m.beid = #{baseQuery.beid}
and b.state in #{item}
AND a.isHold = #{baseQuery.isHold}
AND o.operaDate = #{baseQuery.operDate}
AND a.startTime = #{baseQuery.operTime}
AND a.regOptId = #{baseQuery.regOptId}
AND o.name LIKE CONCAT(CONCAT('%',#{baseQuery.name}),'%')
order by a.operRoomId,a.pcs asc
limit #{baseQuery.pageNo},#{baseQuery.pageSize}

SELECT (select count(*) from bas_dispatch k,bas_reg_opt k1 where k.regOptId=k1.regOptId and k1.operaDate = o.operaDate and k.operRoomId=a.operRoomId AND k.isHold = a.isHold ) seq, o.operaDate,a.startTime,(select br.`name` from b_region br where br.regionId = o.regionId) as region_name ,o.bed,o.hid, o.name as regOptName,o.age,o.sex,o.ageMon,o.ageDay,o.diagnosisName,o.height,o.hypersusceptibility, o.hbsag,o.hcv,o.hiv,o.hp,o.designedOptName,a.optBody,(select codeName from bas_sys_code where groupId='opt_body' and codeValue = a.optBody and beid = #{baseQuery.beid}) as opt_body_name, o.designedAnaesMethodName,(select name from bas_user where bas_user.userName = a.anesthetistId)anesthetist_name, o.operatorName,o.assistantName,m.name as operRoomName, o.realoperationName,o.deptName,o.remark,a.pcs, (select name from bas_user where bas_user.userName = a.instrnurseId1)instrnurse_name1,instrnurseId1 , (select name from bas_user where bas_user.userName = a.circunurseId1)circunurse_name1,circunurseId1, (select name from bas_user where bas_user.userName = a.circuanesthetistId)circuanesthetist_name,circuanesthetistId, (select name from bas_user where bas_user.userName = a.instrnurseId2)instrnurse_name2,instrnurseId2, (select name from bas_user where bas_user.userName = a.circunurseId2)circunurse_name2,circunurseId2, CONCAT_WS(',',(select name from bas_user where bas_user.userName = a.instrnurseId1),(select name from bas_user where bas_user.userName = a.instrnurseId2)) instrnurse_name, CONCAT_WS(',',(select name from bas_user where bas_user.userName = a.circunurseId1),(select name from bas_user where bas_user.userName = a.circunurseId2)) circunurse_name FROM bas_dispatch a left join bas_reg_opt o on a.regOptId = o.regOptId left join bas_operroom m on a.operRoomId = m.operRoomId where 1 = 1 and o.state !='08'
AND a.isHold = #{baseQuery.isHold}
AND o.operaDate = #{baseQuery.operDate}

select count(*) from bas_dispatch,bas_reg_opt
 where bas_dispatch.regOptId=bas_reg_opt.regOptId 
	and bas_reg_opt.operaDate=#{operDate} and bas_dispatch.startTime=#{startTime}
and bas_dispatch.operRoomId=#{roomId}
and bas_reg_opt.state !='08' 
and bas_reg_opt.beid = #{beid}

select count(*) from bas_dispatch,bas_reg_opt
 where bas_dispatch.regOptId=bas_reg_opt.regOptId 
	and bas_reg_opt.operaDate=#{operDate} and bas_dispatch.startTime=#{startTime}
and bas_dispatch.operRoomId=#{roomId}
and bas_reg_opt.state in ('02','03','04') 
and bas_reg_opt.beid = #{beid}

SELECT x.* FROM 
( SELECT codeName,codeValue FROM bas_sys_code WHERE groupId = 'pcs_type' and beid = #{beid} ORDER BY `order` ) `x` 
WHERE  x.codeValue  NOT IN (
	SELECT IFNULL(bas_dispatch.pcs,-1) codeValue FROM bas_dispatch,bas_reg_opt
	WHERE bas_dispatch.regOptId=bas_reg_opt.regOptId 
	AND bas_reg_opt.operaDate=#{operaDate}
	AND bas_dispatch.operRoomId=#{operRoomId}
	AND bas_reg_opt.state IN ('02','03','04','06','07')
	and bas_reg_opt.beid = #{beid}
)  

UNION 
SELECT pcs AS codeName,pcs AS codeValue 
FROM bas_dispatch WHERE regOptId =  #{regOptId}

order by codeValue

select d.* from bas_dispatch d,bas_reg_opt o
where o.regOptId = d.regOptId
and o.state in ('02','03')
and o.operaDate = #{operatDate}
and o.beid = #{beid}

SELECT u1.name AS "instrnurseName1",u3.name AS "instrnurseName2",u2.name AS "circunurseName1",u4.name AS "circunurseName2",b.name AS "anesthetistName",
		u5.name AS "circuanesthetistName",u6.name AS "perfusiondoctorName" FROM
bas_dispatch c LEFT JOIN bas_user b ON c.anesthetistId = b.userName AND b.beid = #{beid}
	LEFT JOIN bas_user u1 ON c.instrnurseId1 = u1.userName AND u1.beid = #{beid} LEFT JOIN bas_user u2 ON c.circunurseId1 = u2.userName AND u2.beid = #{beid} LEFT JOIN bas_user u3 ON c.instrnurseId2 = u3.userName AND u3.beid = #{beid} LEFT JOIN
	bas_user u4 ON c.circunurseId2 = u4.userName AND u4.beid = #{beid} LEFT JOIN bas_user u5 ON c.circuanesthetistId = u5.userName AND u5.beid = #{beid}
	LEFT JOIN bas_user u6 ON c.perfusionDoctorId = u6.userName AND u6.beid = #{beid}
	WHERE c.regOptId = #{regOptId}

SELECT o.regOptId,(select sc.codeName from bas_sys_code sc where sc.groupId='operat_category' and beid = #{baseQuery.beid} and sc.codeValue=o.emergency) as emergencyName,o.deptId,o.`name`,o.sex,o.age,o.ageMon,o.ageDay,o.hid,o.regionName,o.isLocalAnaes, o.deptName,o.bed,o.diagnosisName,o.designedOptName,o.designedAnaesMethodName, o.operatorName,o.assistantName,o.operaDate,a.startTime,a.operRoomId,m.`name` as operRoomName, a.pcs,a.instrnurseId1,a.instrnurseId2,a.circunurseId1,a.circunurseId2,a.optBody,o.remark, a.anesthetistId,a.circuAnesthetistId,a.perfusionDoctorId FROM bas_dispatch a LEFT JOIN bas_reg_opt o ON a.`regOptId` = o.`regOptId` LEFT JOIN bas_operroom m ON a.operRoomId = m.operRoomId AND m.beid = #{baseQuery.beid} WHERE 1 = 1 AND a.beid = #{baseQuery.beid} AND o.state ='02'
AND ( (ISNULL(o.operaDate) OR LENGTH(TRIM(o.operaDate)) < 1 ) OR a.`startTime` IS NULL OR a.`circunurseId1` IS NULL OR a.`circunurseId1`='' or a.operRoomId IS NULL OR a.pcs IS NULL )
AND ( ( NOT(ISNULL( o.operaDate )) AND LENGTH(TRIM(o.operaDate)) > 0 ) AND a.`startTime` IS NOT NULL AND a.`circunurseId1` IS NOT NULL and a.`circunurseId1`!='' AND a.operRoomId IS NOT NULL AND a.pcs IS NOT NULL AND (a.anesthetistId IS NULL or a.anesthetistId ='' ) )
AND o.operaDate = #{baseQuery.operDate}
AND o.`name` LIKE CONCAT(CONCAT('%',#{baseQuery.name}),'%')
AND o.`hid` = #{baseQuery.hid}
ORDER BY o.deptId,a.operRoomId,a.startTime ASC
LIMIT #{baseQuery.pageNo},#{baseQuery.pageSize}
