SELECT DISTINCT(a.time) FROM bas_monitor_display a WHERE a.regOptId = #{regOptId}
AND observeId IN
#{observeId}
order by time desc

	SELECT 
		a.*
	FROM bas_monitor_display a 
	WHERE
	a.regOptId = #{baseQuery.regOptId}
