mapper.delete:
delete from bas_monitor_display
where id = #{id,jdbcType=VARCHAR}
  and time = #{time,jdbcType=TIMESTAMP}

delete from bas_monitor_display 
where time < #{time} 
and regOptId = #{regOptId}
