mapper.select:
     select * from (select r.id,r.outRecordId, r.retreatNumber, r.retreatTime, r.operator, r.retreatName,r.beid,
     o.medicineName,o.firm,o.spec,o.batch,o.minPackageUnit,o.pinYin
     from bas_anaes_medicine_retreat_record r,bas_anaes_medicine_out_record o
     where r.outRecordId = o.id
     and r.retreatType = '1'
     and r.enable=1
     ) x where 1=1






								and DATE_FORMAT(retreatTime,'%Y-%m-%d') >= #{filter.value}




								and DATE_FORMAT(retreatTime,'%Y-%m-%d') <= #{filter.value}




								and outRecordId = #{filter.value}




								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

    SELECT count(id) FROM (
      select * from (       
	  select r.id, r.retreatNumber,r.outRecordId, r.retreatTime, r.operator, r.retreatName, r.beid,
      o.medicineName,o.firm,o.spec,o.batch,o.minPackageUnit,o.pinYin
      from bas_anaes_medicine_retreat_record r,bas_anaes_medicine_out_record o
      where r.outRecordId = o.id
      and r.retreatType = '1'
      and r.enable=1
     ) x where 1=1






								and DATE_FORMAT(retreatTime,'%Y-%m-%d') >= #{filter.value}




								and DATE_FORMAT(retreatTime,'%Y-%m-%d') <= #{filter.value}




								and outRecordId = #{filter.value}




								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	) c

     select * from (
     select x.*,b.designedOptName,b.name,b.sex,b.age,b.hid
      from (select r.id, r.retreatNumber,r.outRecordId, r.retreatTime, r.operator, r.retreatName,r.beid,r.regOptId,
     o.medicineName,o.firm,o.spec,o.batch,o.minPackageUnit,o.pinYin
     from bas_anaes_medicine_retreat_record r,bas_anaes_medicine_out_record o
     where r.outRecordId = o.id
     and r.retreatType = '2'
     and r.enable=1
     ) x ,bas_reg_opt b
     where x.regOptId = b.regOptId
     ) y where 1=1






								and DATE_FORMAT(retreatTime,'%Y-%m-%d') >= #{filter.value}




								and DATE_FORMAT(retreatTime,'%Y-%m-%d') <= #{filter.value}




								and outRecordId = #{filter.value}




								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

    SELECT count(id) FROM (
      select * from (
     select x.*,b.designedOptName,b.name,b.sex,b.age,b.hid
      from (select r.id, r.retreatNumber, r.outRecordId,r.retreatTime, r.operator, r.retreatName,r.beid,r.regOptId,
