mapper.select:
select 

from bas_anaes_medicine_storage
where medicineName = #{medicineCode,jdbcType=VARCHAR}
and firm = #{firm,jdbcType=VARCHAR}
and spec = #{spec,jdbcType=VARCHAR}
and batch = #{batch,jdbcType=VARCHAR}

     select 
     s.id, s.medicineCode, s.medicineName, s.firm, s.spec, s.batch, sum(s.number) as number, 
     s.minPackageUnit, s.productionTime,s.effectiveTime, s.pinYin, s.beid
     from bas_anaes_medicine_storage s where 1=1






								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	group by medicineName,firm,spec
  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

select s.id, s.medicineCode, s.medicineName, s.firm, s.spec, s.batch, s.minPackageUnit, s.productionTime,s.effectiveTime,s.pinYin, (SELECT statisticsNum FROM bas_anaes_medicine_storage_his his WHERE DATE_FORMAT( date_add(his.statisticsTime, interval 1 MONTH) ,'%Y-%m') = #{queryMonth} and his.beid = s.beid and his.storageId = s.id) as lastMonthAllNum, (select sum(i.number) from bas_anaes_medicine_in_record i where i.`status` = 1 and DATE_FORMAT(i.operateTime,'%Y-%m') = #{queryMonth} and i.beid = s.beid and i.medicineName=s.medicineName and i.firm =s.firm and i.spec = s.spec and i.batch = s.batch) as monthAllInNum, (select sum(ou.outNumber) from bas_anaes_medicine_out_record ou where ou.`enable` = 1 and DATE_FORMAT(ou.outTime,'%Y-%m') = #{queryMonth} and ou.beid = s.beid and ou.medicineName=s.medicineName and ou.firm =s.firm and ou.spec = s.spec and ou.batch = s.batch ) as monthAllOutNum, (select sum(ou.retreatNumber) from bas_anaes_medicine_out_record ou where ou.`enable` = 1 and DATE_FORMAT(ou.outTime,'%Y-%m') = #{queryMonth} and ou.beid = s.beid and ou.medicineName=s.medicineName and ou.firm =s.firm and ou.spec = s.spec and ou.batch = s.batch ) as monthAllRetreatNum, ( (select IFNULL(sum(ou.loseNumber),0) from bas_anaes_medicine_out_record ou where ou.`enable` = 1 and DATE_FORMAT(ou.outTime,'%Y-%m') = #{queryMonth} and ou.beid = s.beid and ou.medicineName=s.medicineName and ou.firm =s.firm and ou.spec = s.spec and ou.batch = s.batch) + (select IFNULL(sum(los.loseNumber),0) from bas_anaes_medicine_lose_record los where los.loseType='3' and los.`enable` = 1 and DATE_FORMAT(los.loseTime,'%Y-%m') = #{queryMonth} and los.beid = s.beid and los.storageId = s.id) ) as monthAllLoseNum, (SELECT statisticsNum FROM bas_anaes_medicine_storage_his his WHERE DATE_FORMAT(his.statisticsTime,'%Y-%m') = #{queryMonth} and his.beid = s.beid and his.storageId = s.id) as monthAllNum from bas_anaes_medicine_storage s where 1=1
and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))
and beid = #{filter.value}
AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')
order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}
limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

SELECT count(id) FROM ( select
from bas_anaes_medicine_storage where 1 = 1
and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))
and beid = #{filter.value}
AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')
) x

     select 
     medicineCode, medicineName, firm, spec, batch, minPackageUnit,outTime,receiveName,outType,enable,pinYin,beid,
     sum(outNumber) as allOutNumber,
     sum(retreatNumber) as allRetreatNumber,
     sum(loseNumber) as allLoseNumber,
     sum(actualNumber) as allActualNumber
     from bas_anaes_medicine_out_record  where `enable` = 1






								and  DATE_FORMAT(outTime,'%Y-%m-%d') >= #{filter.value}




								and DATE_FORMAT(outTime,'%Y-%m-%d') <= #{filter.value}




								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	group by medicineName,firm,spec
  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

    SELECT count(id) FROM (
	 select 
      id, medicineCode, medicineName, firm, spec, batch, minPackageUnit,outTime,receiveName,outType,enable,pinYin,beid
     from bas_anaes_medicine_out_record 
	 where `enable` = 1






								and DATE_FORMAT(outTime,'%Y-%m-%d') >= #{filter.value}




								and DATE_FORMAT(outTime,'%Y-%m-%d') <= #{filter.value}




								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	group by medicineName,firm,spec
	) x

     select 

     from bas_anaes_medicine_storage where number > 0






								and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

SELECT count(id) FROM ( select
from bas_anaes_medicine_storage where number > 0
and (medicineName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))
and beid = #{filter.value}
AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')
) x
