mapper.select:
SELECT a.deviceId, a.deviceModel AS deviceName, a.roomId, a.ipAddress AS ip, a.serialPort, a.deviceType AS modelId, b.protocol, b.netPort, a.baudRate, b.startBit, b.dataBit, b.stopBit, b.broadPort, b.parityBit, b.interfaceType, a.`status`, a.`beid` FROM bas_device_config a, bas_device_specification b WHERE a.deviceId = b.deviceId AND a.`beid` = b.`beid` AND a.enable = '1'
AND a.roomId = #{roomId}
and a.beid = #{beid}
AND a.roomId IS NULL group by deviceId

mapper.update:
UPDATE bas_device_config SET 
	status = #{status}
WHERE deviceId = #{deviceId} 
and beid = #{beid}
