select 

from bas_operation_people
where 1=1 
AND operatorId = #{operatorId} 
AND code LIKE CONCAT('%',#{code},'%') 
AND name LIKE CONCAT('%',#{name},'%') 
AND pinYin LIKE CONCAT('%',#{pinYin},'%') 
AND enable = #{enable} 
AND region = #{region} 
AND beid = #{beid}

