mapper.select:
SELECT * FROM doc_packages_item a left join bas_reg_opt b on a.regOptId = b.regOptId where 1=1 ${filter} 
and b.beid = #{beid}
order by flag DESC

mapper.update:
UPDATE doc_packages_item set 
chargeAmount = #{chargeAmount},
isCharge = #{isCharge},
discount = #{discount}
where pkItId = #{pkItId}
