mapper.select:
SELECT DISTINCT *
FROM doc_pre_visit d
WHERE d.regOptId = #{regOptId}

SELECT DISTINCT *
FROM doc_pre_visit d
WHERE d.preVisitId = #{id}

SELECT
	DISTINCT a.regOptId,a.name,a.hid,a.operaDate
FROM doc_pre_visit p left join bas_reg_opt a on p.regOptId = a.regOptId left join bas_dispatch c on a.regOptId = c.regOptId
WHERE 1 = 1 and ( p.processState != 'END' or p.processState is null)
and (a.archState !='AR' or a.archState is null)

AND (c.anesthetistId = #{loginName} or c.circunurseId1 = #{loginName} or c.circunurseId2 = #{loginName} or c.instrnurseId1 = #{loginName} or c.instrnurseId2 = #{loginName}) 

	 and a.state in #{item}
	 and a.beid = #{beid}
 order by a.${searchConditionFormBean.sort} ${searchConditionFormBean.orderBy}
