mapper.select:
SELECT
	a.medEventId,a.thickness,a.thicknessUnit,a.flow,a.flowUnit,a.diluentQuant,a.startTime,a.createUser,a.docType,
	(select name from bas_user where bas_user.userName = a.createuser and bas_user.beid = #{searchBean.beid} limit 1 ) createUserName,
	a.endTime,a.ended,a.medicineId,a.occurHour,a.docId,a.isContinued,a.priceId,
	b.name,p.spec,b.dosageUnit,a.medTakeWayId,m.name as medTakeWayName,p.firm,a.reason,a.dosage,a.durable,a.pcorec,a.tcivalue,a.tciunit
FROM evt_medicalevent a left join bas_medicine b on a.medicineId=b.medicineId 
left join bas_price p on p.priceId = a.priceId
left join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId
WHERE 1 = 1

	AND a.docId = #{searchBean.docId}


	AND a.docType = #{searchBean.docType}


	AND a.type = #{searchBean.type}


	AND a.medicineId = #{searchBean.code}


	AND a.durable = #{searchBean.durable}

group by a.medEventId,b.code
order by a.startTime,a.medEventId

SELECT 
	a.*
FROM evt_medicalevent a where 1 = 1

	AND a.docId = #{searchBean.docId}


	AND a.medEventId = #{searchBean.id}

SELECT b.medicineId code,b.`name`,a.`durable` FROM evt_medicalevent a join bas_medicine b on a.medicineId=b.medicineId join bas_price p on ( p.code = b.code and p.beid = b.beid ) join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId WHERE 1 = 1
AND a.docId = #{searchBean.docId}
AND a.type = #{searchBean.type}
group by a.`durable`,b.medicineId,b.`name` order by a.startTime,a.medEventId

select * from evt_medicalevent where docId = #{searchBean.docId}
	and durable='1' and medicineId = #{medicineId}

		and medEventId != #{searchBean.id}

SELECT a.medEventId,a.thickness,a.thicknessUnit,a.flow,a.flowUnit,a.diluentQuant,a.startTime,a.createUser,a.docType, (select name from bas_user where bas_user.userName = a.createuser and bas_user.beid = #{beid} limit 1 ) createUserName, a.endTime,a.ended,a.medicineId,a.occurHour,a.docId,a.isContinued,a.priceId, b.name,p.spec,b.dosageUnit,a.medTakeWayId,m.name as medTakeWayName,p.firm,a.reason,a.dosage,a.durable,a.pcorec,a.tcivalue,a.tciunit FROM evt_medicalevent a left join bas_medicine b on a.medicineId=b.medicineId left join bas_price p on p.priceId = a.priceId left join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId WHERE 1 = 1
AND a.docId = #{docId}
and a.medEventId in #{item}
order by a.startTime,a.medEventId

SELECT b.medicineId code,b.`name`,sum(dosage) dosage,b.dosageUnit FROM evt_medicalevent a join bas_medicine b on a.medicineId=b.medicineId join bas_price p on (p.code = b.code AND p.beid = b.beid ) join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId WHERE 1 = 1
AND a.docId = #{searchBean.docId}
group by b.medicineId,b.`name` order by a.startTime,a.medEventId

SELECT b.medicineId code,b.`name`,sum(dosage) dosage,b.dosageUnit FROM evt_medicalevent a join bas_medicine b on a.medicineId=b.medicineId join bas_price p on (p.code = b.code AND p.beid = b.beid ) join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId WHERE 1 = 1
AND a.docId = #{searchBean.docId}
AND a.medicineId = #{searchBean.code}
AND a.type = #{searchBean.type}
group by b.medicineId,b.`name` order by a.startTime,a.medEventId

mapper.delete:
delete from evt_medicalevent
where docId = #{docId,jdbcType=VARCHAR} 
and   type = #{type,jdbcType=INTEGER}
