mapper.select:
SELECT 
	a.*,(select bas_sys_code.codeName from bas_sys_code where groupId = 'opt_body' and codeValue = a.optBody and beid = #{searchBean.beid}) as opt_body_name,
	(select bas_sys_code.codeName from bas_sys_code where groupId = 'opt_body' and codeValue = a.optBodyOld and beid = #{searchBean.beid} ) as opt_body_old_name
FROM evt_oper_body_event a  where 1 = 1 

	AND a.docId = #{searchBean.docId}


	AND a.optBodyEventId = #{searchBean.id}

mapper.update:
update evt_oper_body_event
set docId = #{docId,jdbcType=VARCHAR},
  startTime = #{startTime,jdbcType=TIMESTAMP},
  optBody = #{optBody,jdbcType=VARCHAR},
  optBodyOld = #{optBodyOld,jdbcType=VARCHAR}
where optBodyEventId = #{optBodyEventId,jdbcType=VARCHAR}
