mapper.select:
SELECT a.*,b.eventName,b.eventDesc,b.units,b.mustShow
			FROM
				bas_anaes_monitor_config a,bas_monitor_config b
			WHERE a.realEventId = b.eventId 
			and a.beid = b.beid
			and a.beid = #{baseQuery.beid}
			and a.regOptId =  #{baseQuery.regOptId}
			and	a.position = #{baseQuery.position}
			group by a.eventId

SELECT a.*,b.eventName,b.eventDesc
			FROM
				bas_anaes_monitor_config a,bas_monitor_config b
			WHERE a.realEventId = b.eventId 
			and a.beid = b.beid
			and a.beid = #{baseQuery.beid}
			and regOptId =  #{baseQuery.regOptId} 
			and a.eventId = #{baseQuery.eventId}

SELECT a.*,b.`eventName`,b.`eventDesc` FROM bas_anaes_monitor_config a ,bas_monitor_config b 
WHERE a.realEventId = b.`eventId`
and a.beid = b.beid
and a.beid = #{baseQuery.beid}
AND regOptId = #{baseQuery.regOptId} 
AND a.eventId  = #{baseQuery.eventId}

SELECT a.* FROM bas_anaes_monitor_config a,bas_monitor_config_default b
	WHERE a.eventId = b.`eventId`
	and a.beid = b.beid
	and a.beid = #{baseQuery.beid}
	AND a.`regOptId` = #{baseQuery.regOptId}
	AND b.`eventName` = #{baseQuery.name};

select * from( SELECT #{baseQuery.regOptId} AS regOptId, b.eventName, IFNULL(a.color,b.color) color, IFNULL(a.iconId,b.iconId) iconId, b.eventId, b.position, IFNULL(a.`max`,b.`max`) max, IFNULL(a.`min`,b.`min`) min, IF (a.eventId > 0, '1', IF(b.mustShow = '1','1',0)) checked, b.mustShow, b.`units` FROM (SELECT * FROM bas_monitor_config WHERE beid = #{baseQuery.beid}) b LEFT JOIN (SELECT * FROM bas_anaes_monitor_config WHERE regOptId = #{baseQuery.regOptId} AND `position` = #{baseQuery.position}) a ON a.realEventid = b.eventId and a.beid = b.beid where b.`position` = #{baseQuery.position}) t where 1 = 1
and t.checked = #{baseQuery.enable}
ORDER BY t.eventId
