mapper.select:
select

from bas_menu
where 1=1
AND name = #{name} 
AND type = #{type} 
AND target = #{target} 
AND url = #{url} 
AND isLeftMenu = #{isLeftMenu} 
AND parentId = #{parentId} 
AND permission LIKE CONCAT('%',#{permission},'%') 
AND urlType = #{urlType} 
AND module = #{module} 
AND enable = #{enable} 
AND beid = #{beid} 
ORDER BY sort ASC 
LIMIT #{pageNo},#{pageSize}

select

from bas_menu
where id = #{id,jdbcType=VARCHAR}
and beid =
#{beid,jdbcType=VARCHAR}

select  from bas_menu
  where parentId = #{parentId,jdbcType=VARCHAR}
  and beid = #{beid,jdbcType=VARCHAR}
  order by sort
