mapper.select:
SELECT

FROM bas_user
WHERE userName = #{userName} and beid = #{beid}

SELECT count(*)
FROM bas_user a join bas_user_role b on a.userName = b.userId LEFT JOIN bas_role r on b.roleId = r.id 
WHERE 1=1 ${sql} and a.locked = 0 and a.beid = #{beid} and b.beid = #{beid}

    select 

    from bas_user
    where 1=1 
    AND userName = #{userName} 
    AND name LIKE CONCAT('%',#{name},'%') 
    AND mobile LIKE CONCAT('%',#{mobile},'%') 
    AND email LIKE CONCAT('%',#{email},'%') 
    AND createUser = #{createUser} 
    AND enable = #{enable} 
    AND locked = #{locked} 
    AND salt = #{salt} 
    AND token = #{token} 
    AND userType = #{userType} 
    AND pinYin LIKE CONCAT('%',#{pinYin},'%') 
    AND professionalTitle = #{professionalTitle} 
    AND executiveLevel = #{executiveLevel} 
    AND beid = #{beid} 
	LIMIT #{pageNo},#{pageSize}

SELECT a.userName,a.name, a.mobile,a.userType,a.loginIp,a.loginDate ,a.createUser,a.pinYin,b.roleId as "roleId", a.professionalTitle,a.executiveLevel,a.createDate FROM bas_user a join bas_user_role b on a.userName = b.userId WHERE 1 = 1
AND a.userType = #{baseQuery.userType}
AND a.beid = #{baseQuery.beid}

mapper.delete:
delete from bas_user
where userName = #{userName,jdbcType=VARCHAR}
  and beid = #{beid,jdbcType=VARCHAR}

mapper.update:
UPDATE bas_user SET 
	enable = #{enable}
WHERE userName = #{userName} and beid = #{beid}

	update bas_user set token = #{token,jdbcType=VARCHAR}
	 where userName = #{userName,jdbcType=VARCHAR}
    and beid = #{beid,jdbcType=VARCHAR}
