mapper.select:
SELECT a.medEventId,a.thickness,a.thicknessUnit,a.flow,a.flowUnit,a.diluentQuant,a.starttime,a.createuser, (select name from bas_user where bas_user.username = a.createuser) createuserName, a.endtime,a.ended,a.medicineId,a.occurHour,a.isContinued,a.priceId, b.name,p.spec,b.dosageUnit,a.medTakeWayId,m.name as medTakeWayName,p.firm,a.reason,a.dosage,a.durable FROM tmp_medicine_event a left join bas_medicine b on a.medicineId=b.medicineId left join bas_price p on p.priceId = a.priceId left join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId WHERE 1 = 1
AND a.type = #{searchBean.type}
AND a.medicineId = #{searchBean.code}
group by a.medEventId,b.code order by b.code

SELECT b.medicineId code,b.`name` FROM tmp_medicine_event a left join bas_medicine b on a.medicineId=b.medicineId left join bas_price p on p.code = b.code left join bas_medical_take_way m on m.medTakeWayId = a.medTakeWayId WHERE 1 = 1
AND a.type = #{searchBean.type}
group by b.medicineId,b.`name`

SELECT 
	a.*
FROM tmp_medicine_event a where  a.medEventId = #{medEventId}

select * from tmp_medicine_event where 

durable='1' and medicineId = #{medicineId}

		and medEventId != #{searchBean.id}
